package com.github.dkoval.leetcode.challenge

import com.github.dkoval.leetcode.challenge.SeparateBlackAndWhiteBalls.SeparateBlackAndWhiteBallsRev1
import org.junit.jupiter.api.Assertions.assertEquals
import org.junit.jupiter.api.Nested
import org.junit.jupiter.api.extension.ExtensionContext
import org.junit.jupiter.params.ParameterizedTest
import org.junit.jupiter.params.provider.Arguments
import org.junit.jupiter.params.provider.ArgumentsProvider
import org.junit.jupiter.params.provider.ArgumentsSource
import java.util.stream.Stream

internal class SeparateBlackAndWhiteBallsTest {

    class InputArgumentsProvider : ArgumentsProvider {

        override fun provideArguments(context: ExtensionContext): Stream<out Arguments> = Stream.of(
            Arguments.of("101", 1L),
            Arguments.of("100", 2L),
            Arguments.of("0111", 0L),
            Arguments.of(
                "",
                2500000000L
            )
        )
    }

    @Nested
    inner class SeparateBlackAndWhiteBallsRev1Test {

        @ParameterizedTest
        @ArgumentsSource(InputArgumentsProvider::class)
        fun `should return the minimum number of steps to group all the black balls to the right and all the white balls to the left`(
            s: String,
            expected: Long
        ) {
            SeparateBlackAndWhiteBallsRev1().test(s, expected)
        }
    }
}

private fun SeparateBlackAndWhiteBalls.test(s: String, expected: Long) {
    val actual = minimumSteps(s)
    assertEquals(expected, actual)
}
